import React,{useState} from "react";
import {Routes, Route, useNavigate} from "react-router-dom"


function ForgotPassword(){
    const url = "http://localhost:9292";
    const[email, setEmail] = useState("");
    //const[recoveryQuestion, setRecoveryQuestion] = useState("");
    const[answer, setAnswer] = useState("");
    const[password, setPassword] = useState("");
    const[userData, setUserData] = useState({});
    const history = useNavigate()

    function handleOnChange(event){
        if (event.target.name === "email"){
            setEmail(event.target.value);
        }
        else if (event.target.name === "recoveryQuestion"){
            setRecoveryQuestion(event.target.value);
        }
        else if (event.target.name === "recoveryQuestionAnswer"){
            setAnswer(event.target.value);
        }
        else{
            setPassword(event.target.value);
        }  
      }

      function handleForgotPassword(event){
        const loginData = {
            email: email,
        }

        event.preventDefault()
        fetch(`${url}/forgot-password`, {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            }, 
            body: JSON.stringify(loginData)
        })
        .then(response => response.json())
        .then(data => {
            //console.log(data)
            if(Object.values(data)[0] === "This email isn't connected to any user account!"){

                //<Alert type="error" message="Invalid Username or Password"/>
                alert("This email isn't connected to any user account!");
            }
            else{

                localStorage.setItem("loginData", JSON.stringify(data));

                setUserData(JSON.parse(localStorage.getItem("loginData")), ()=> console.log(this.state))
                history("/forgot-password/2")
            }  
        });
      }

    return(
        <div>
            <h2>FORGOT PASSWORD</h2>
            <Routes>

            <Route path='/1' element ={
                <div>
                    <form className="form" onSubmit={handleForgotPassword}>
                        <input type={"email"} name="email" placeholder="Email" value={email} required onChange={handleOnChange}/>
                        <button type="submit">Next</button> 
                    </form>
                    <p>Step 1 of 3</p>
                </div>}/>

            <Route path='/2' element ={
            <div>
                <form className="form" onSubmit={handleForgotPassword}>
                <input type={"text"} name="recoveryQuestion" placeholder="Recovery Question" value={userData} readOnly onChange={handleOnChange}/>
                <input type={"text"} name="recoveryQuestionAnswer" placeholder="Answer" value={answer} required onChange={handleOnChange}/>               
                <button type="submit">Next</button> 
                </form>
                <p>Step 2 of 3</p>
            </div>}/>

            </Routes>

        </div>
    );
}

export default ForgotPassword;
