import React, {useEffect, useState} from "react";
import { useNavigate, Link, Routes, Router} from "react-router-dom";
import Journal from "./Journal"

let userData = JSON.parse(localStorage.getItem("userData"));
const loginStatus = localStorage.getItem("loginStatus");
function JournalList(){
    const url = "http://localhost:9292";
    const history = useNavigate();
    const[allJournals, setAllJournals] = useState([]);

    useEffect(()=>{
        if(loginStatus === "true"){
            userData = JSON.parse(localStorage.getItem("userData"));
            fetch(`${url}/journal-list/${userData.id}`)
            .then(response => response.json())
            .then(journals => setAllJournals(journals));
        }
        else{
            history("/")
        }
    }, [])

        return(
            <div>
                <h2>WELCOME {userData.username.toUpperCase()}</h2>
                <div id= "logoutContainer">
                    <Link id={"logout"} to={'/'} onClick={()=>{
                        localStorage.clear()
                        history("/")
                    }}>logout</Link>
                </div>

                <div>
                    {
                        allJournals.map((journal)=>{
                            <Journal key={journal.id} 
                            title={journal.title}
                            body={journal}

                            ></Journal>
                        })
                    }
                </div>

            </div>
        )
}

export default JournalList
