import React from "react";
import Journal from "./Journal"
import Search from "./Search";
import {Link} from "react-router-dom";

function JournalList({allJournals, setCurrentItem, handleFilteredData, handleDelete}){
        return(
                <div className="jContainer">
                    <Search allJournals={allJournals} handleSearchData={handleFilteredData} />
                    <h4>All Journal Entries</h4>

                    {
                        allJournals?.map((journal)=> <Journal
                            key={journal.id} 
                            title={journal.title}
                            body={journal.body}
                            createdAt={journal.created_at.slice(0, 10)}
                            updatedAt={journal.updated_at.slice(0, 10)}
                            editbtn={<div className="editWrapper"><Link className={"editlink"} to={"/"}  onClick={()=>setCurrentItem(journal)}>Edit</Link></div>}
                            deletebtn={<div className="editWrapper"><Link className={"deletelink"} to={"/"} onClick={()=>{handleDelete(journal.id)}}>Delete</Link></div>}
                            ></Journal>
                        )
                    }
                </div>
        )
}

export default JournalList
